$(document).ready(function(){$(".CustomerDetails").click(function(){var n=$(this).attr("data-id");$.ajax({url:"/Customer/GetCustomerDetails",data:{id:n},type:"GET",success:function(n){$("#CustomerModalBody").html(n);$("#CustomerModalWindow").modal("show")},error:function(){swal({text:"Error loading customer details",icon:"error",button:"ok"})}})});$("#exportBtn").click(function(n){n.preventDefault();var t=window.location.href.split("?")[0],i=t+"Customer/ExportCustomersCSV?downloadCSV=true";window.location.href=i});$(".role-dropdown").change(function(){var n=$(this).data("user-id"),t=$(this).val();$.ajax({type:"POST",url:"/Account/ChangeRole",data:{Id:n,role:t},success:function(){swal.fire({text:"Role changed successfully.",icon:"success",button:"ok"})},error:function(){swal.fire({text:"An error occurred while changing the role.",icon:"error",button:"ok"})}})});$(document).on("click",".RoleDelete-Btn",function(n){n.preventDefault();var t=$(this).data("id");Swal.fire({title:"Are you sure?",text:'You do not be able to revert this!"',icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Yes, delete it!"}).then(n=>{n.isConfirmed&&$.ajax({url:"/Roles/DeleteRole",type:"POST",data:{id:t},success:function(){var n=$("#"+t);n.remove()},error:function(n){console.log(n.responseText)}})})})});