$(document).ready(function(){$(".CustomerDetails").click(function(){var n=$(this).attr("data-id");$.ajax({url:"/Customer/GetCustomerDetails",data:{id:n},type:"GET",success:function(n){$("#CustomerModalBody").html(n);$("#CustomerModalWindow").modal("show")},error:function(){swal({text:"Error loading customer details",icon:"error",button:"ok"})}})});$("#exportBtn").click(function(n){n.preventDefault();var t=window.location.href.split("?")[0],i=t+"Customer/ExportCustomersCSV?downloadCSV=true";window.location.href=i});$(".role-dropdown").change(function(){var n=$(this).data("userid"),t=$(this).val();$.ajax({type:"POST",url:"/Account/ChangeRole",data:{userId:n,roleName:t},success:function(){swal({text:"Role changed successfully.",icon:"success",button:"ok"})},error:function(){swal({text:"An error occurred while changing the role.",icon:"error",button:"ok"})}})})});